    //setManager(xx);
    console.log('manager');
    console.log(manager);
    console.log('players');
    console.log(players);
    console.log('balance');
    console.log(balance);
  }

  getManager();

  return(
    <div><h1>Hi Welcome</h1></div>
  );
}
export default App;
